/* * ResultWriter.java * * Created on 18 sie 2007, 18:22 * * To change this template, choose Tools | Template Manager * and open the template in the editor. */package treecmp;import java.io.File;import java.io.FileWriter;import java.io.IOException;/** * * @author Damian */public class ResultWriter {    private String result;    public boolean writeToFile;    private String fileName;    private File file;    private FileWriter filewriter;        /** Creates a new instance of ResultWriter */   public ResultWriter() {    }    public ResultWriter(String txt, boolean writeToFile_, String fileName_) {        this.result = txt;        this.writeToFile = writeToFile_;        this.fileName = fileName_;    }    public ResultWriter(String txt) {        this.result = txt;        this.writeToFile = false;    }         public ResultWriter(String fileName_, boolean writeToFile_ ) {        this.writeToFile = writeToFile_;        this.fileName = fileName_;    }        public void init() {        if (this.writeToFile == true) {            this.file = new File(this.fileName);            try {                this.filewriter = new FileWriter(this.file);            } catch (IOException ex) {                ex.printStackTrace();            }        }    }    public void close() {        if (this.filewriter != null) {            try {             this.filewriter.close();            } catch (IOException ex) {                ex.printStackTrace();            }        }    }    public void write() {        if (this.writeToFile == false) {            System.out.println(this.result);        } else {            try {                this.filewriter.write(this.result + "\r\n");            } catch (IOException ex) {                ex.printStackTrace();            }        }    }    public void write_pure() {        if (this.writeToFile == false) {            System.out.println(this.result);        } else {            try {                this.filewriter.write(this.result);            } catch (IOException ex) {                ex.printStackTrace();            }        }    }    public void setText(String text) {        this.result = text;    }    public void setFileName(String text) {        this.fileName = text;    }    public void isWriteToFile(boolean is) {        this.writeToFile = is;    }}